{"changed":true,"filter":false,"title":"books_controller.rb","tooltip":"/app/controllers/books_controller.rb","value":"class BooksController < ApplicationController\n  before_action :authenticate_user!\n  before_action :ensure_correct_user, only: [:edit, :update, :destroy]\n  impressionist :actions=>[:show, :index]\n\n  def show\n    @book = Book.find(params[:id])\n    @book_comment = BookComment.new\n  end\n\n  def index\n    @book = Book.new\n    to = Time.current.at_end_of_day\n    from = (to - 6.day).at_beginning_of_day\n    @books = Book.includes(:favorited_users).sort{|a,b| b.favorited_users.where(created_at: from...to).size <=> a.favorited_users.where(created_at: from...to).size}\n  end\n\n  def create\n    @book = Book.new(book_params)\n    @book.user_id = current_user.id\n    if @book.save\n      redirect_to book_path(@book), notice: \"You have created book successfully.\"\n    else\n      @books = Book.all\n      render 'index'\n    end\n  end\n\n  def edit\n    @book = Book.find(params[:id])\n  end\n\n  def update\n    if @book.update(book_params)\n      redirect_to book_path(@book), notice: \"You have updated book successfully.\"\n    else\n      render \"edit\"\n    end\n  end\n\n  def destroy\n    @book.destroy\n    redirect_to books_path\n  end\n\n  private\n\n  def book_params\n    params.require(:book).permit(:title, :body)\n  end\n\n  def ensure_correct_user\n    @book = Book.find(params[:id])\n    unless @book.user == current_user\n      redirect_to books_path\n    end\n  end\nend\n","undoManager":{"mark":21,"position":21,"stack":[[{"start":{"row":25,"column":10},"end":{"row":26,"column":0},"action":"insert","lines":["",""],"id":2},{"start":{"row":26,"column":0},"end":{"row":26,"column":4},"action":"insert","lines":["    "]},{"start":{"row":26,"column":4},"end":{"row":26,"column":5},"action":"insert","lines":["@"]},{"start":{"row":26,"column":5},"end":{"row":26,"column":6},"action":"insert","lines":["b"]}],[{"start":{"row":26,"column":6},"end":{"r